// import React from "react";
import React, { Component } from 'react'; //lets you export multiple times

import Card from "./components/Card";
import Header from "./components/Header";
import Wrapper from "./components/Wrapper";

import pictures from "./pictures.json";
// import { Container, Row, Col } from 'reactstrap';

class App extends Component {  
  constructor(props) {
    super(props);
    this.state = {
      pictures: pictures,
      currentScore: 0,
      highscore: 0
    };
    this.checkIfClicked = this.checkIfClicked.bind(this);
  }

  gameOver = () => {
    if (this.state.score > this.state.highscore) {
      this.setState({highscore: this.state.score}, function() {
        console.log(this.state.highscore);
      });
    }
    this.state.pictures.forEach(card => {
      card.count = 0;
    });
    alert(`Game Over :( \nscore: ${this.state.score}`);
    this.setState({score: 0});
    return true;
  }

  checkIfClicked(id) {
    // create a copy of the cards and use a random method to sort the array
    let clickedCard = this.state.pictures.filter(pictures => pictures.id === id)[0];
    let picturesCopy = this.state.pictures.slice().sort(function(a, b){return 0.5 - Math.random()});
    // change boolean "clicked" to true if clicked 
    if (!clickedCard.clicked) {
      clickedCard.clicked = true;
      picturesCopy[picturesCopy.findIndex((pictures) => pictures.id === id)] = clickedCard;

      // correct guess, increase score
      this.setState({
        pictures: picturesCopy,
        currentScore: this.state.currentScore + 1,
      });
    }

    // if a card has been clicked already, then set its click to false and reset the game
    else {

      let resetPicturesCopy = picturesCopy.map((pictures) => {
        return {
          id: pictures.id,
          image: pictures.image,
          clicked: false,
        }
      });
      this.setState({
        pictures: resetPicturesCopy,
        currentScore: 0,
      });
    } 
  }

  // render the header, score, wrapper, and footer on the page using the current state values
  render() {
    return (
      <div className="container">
        <Header currentScore={this.state.currentScore} highscore={this.state.highscore} />
        <Wrapper>  
          {this.state.pictures.map(pictures => (
            <Card
            // clickCount={this.clickCount}
              checkIfClicked={this.checkIfClicked}
              id={pictures.id}
              image={pictures.image}
              key = {pictures.id}
              />
          ))}
        </Wrapper>
      </div>
    );
  }
}

export default App;



